import React, {useState} from 'react';
// import memesData from "../memesData";
import memesData from "../memesData1";

const MainForm = () => {

    const {memes} = memesData.data;

    const [memeImage, setMemeImage] = useState('') ;

    function getMemeImage(e) {
        e.preventDefault();
        let randomValue = Math.floor((Math.random()) * memes.length);
        setMemeImage(memeImage => memes[randomValue].url)
    }


    // const elements = arr.map(item => <li>{item}</li>)

    const itemsArr = ['Item 1', 'Item 2'];

    const [array, setItem] = useState(itemsArr);

    function addItem(e) {
        e.preventDefault();
        setItem(prevArray => {
            return [...prevArray, `Item ${prevArray.length + 1}`];
        });
    }

    const elements = array.map(item => <li>{item}</li>)

    return (
        <main>
            <ul>
                {elements}
            </ul>
            <button onClick={addItem}>add item</button>
            <form className='form'>
                <input className="form--input" placeholder="Top text"/>
                <input className="form--input" placeholder="Bottom text"/>
                <button onClick={getMemeImage} className="form--button">Get a new meme image ðŸ–¼</button>
            </form>
            <div className="meme-img">
                <img src={memeImage} alt=""/>
            </div>
        </main>
    );
};

export default MainForm;
