import React, {useState} from 'react';
// import memesData from "../memesData";
import memesData from "../memesData1";

const MainForm = () => {

    const [meme, setMeme] = useState({
        topText: '',
        bottomText: '',
        randomImage: 'https://i.imgflip.com/265j.jpg'
    }) ;

    const [allMemeImages, setAllMemeImages] = useState(memesData);

    function getMemeImage(e) {
        e.preventDefault();

        const {memes} = allMemeImages.data;
        const randomValue = Math.floor((Math.random()) * memes.length);
        const url = memes[randomValue].url;

        setMeme(prevState => {
            return {
                ...prevState,
                randomImage: url
            }
        })
    }

    const [formData, setFormData] = useState({topText: '', bottomText: ''});

    function changeText(e) {
        setFormData(prevState => {
            return {
                ...prevState,
                [e.target.name]: e.target.value
            }
        })
    }

    return (
        <main>
            <form className='form'>
                <h1>{formData}</h1>
                <input onChange={changeText} name="topText" className="form--input" placeholder="Top text"/>
                <input onChange={changeText} name="bottomText" className="form--input" placeholder="Bottom text"/>
                <button onClick={getMemeImage} className="form--button">Get a new meme image ðŸ–¼</button>
            </form>
            <div className="meme-img">
                <img src={meme.randomImage} alt=""/>
            </div>
        </main>
    );
};

export default MainForm;
